import axios from 'axios'

export default {
  name: 'ContactPage',
  data() {
    return {
      form: {
        name: '',
        email: '',
        phone: '',
        message: ''
      }
    }
  },
  methods: {
    async onSubmit() {
      try {
        await axios({
          method: 'POST',
          url: 'http://localhost:1337/contacts',
          data: this.form
        })
        window.alert('发送成功！')
        this.form = {
          name: '',
          email: '',
          phone: '',
          message: ''
        }
      } catch (e) {
        window.alert('发送失败！')
      }
    }
  }
}
